    public static List<String> parse4ImgData(String imgdata){
        imgdata=imgdata.replace("[","").replace("]","").replace("\"","");
        List<String> data= Arrays.asList(imgdata.split("\\,"));
        int half=data.size()/2;
        List<String> name=data.subList(0,half);
        return name;
    }
